mapper.insert:
INSERT INTO enrollment (
    enrollment_id, student_id, course_id, teacher_id,
    year, term, status
)
VALUES (
           UUID(), #{studentId}, #{courseId}, #{teacherId},
           #{year}, #{term}, #{status}
       )

mapper.delete:
DELETE FROM enrollment
WHERE student_id = #{studentId}
          AND course_id = #{courseId}
          AND year = #{year}
  AND term = #{term}
